def confusionmatrix(data, dists):
    predictions = []
    actual = []
    for i in range(int(len(data) * len(dists) / 10)):
        predictions.append(predictdigit(data[i], dists))
    for j in range(10):
        for k in range(220):
            if j == 9 and k == 219:
                continue
            else:
                actual.append(j)
    return confusion_matrix(actual, predictions)


